mapper.insert:
INSERT INTO board_table (
	board_num
	, content
	, write_id
)
VALUES (
	board_num_seq.nextval
	, #{content}
	, #{writeId}
)

mapper.select:
SELECT like_num AS board_num
FROM board_like_table
WHERE like_num = #{boardNum}
	AND like_user = #{writeId}

mapper.insert:
INSERT INTO board_like_table(
	like_num
	, like_user
) VALUES(
	#{boardNum}
	, #{writeId}
)

mapper.update:
UPDATE board_table
SET content = #{content}
WHERE board_num = #{boardNum}

mapper.select:
SELECT COUNT(*) FROM board_table BOARD LEFT JOIN user_tb USER_ ON BOARD.write_id = USER_.user_id
LEFT JOIN board_like_table LIKE_TB ON BOARD.board_num = LIKE_TB.like_num WHERE LIKE_TB.like_user = #{sessUserId}
WHERE BOARD.write_id = #{sessUserId}

SELECT * FROM ( SELECT ROW_NUMBER() OVER(ORDER BY board_num DESC) AS ROW_NUM , BOARD.board_num , BOARD.board_parent_num , BOARD.content , BOARD.write_id , TO_CHAR(BOARD.write_date, 'YYYY-MM-DD') AS write_date , BOARD.reply_cnt , BOARD.like_cnt , USER_.user_name FROM board_table BOARD LEFT JOIN user_tb USER_ ON BOARD.write_id = USER_.user_id
LEFT JOIN board_like_table LIKE_TB ON BOARD.board_num = LIKE_TB.like_num WHERE LIKE_TB.like_user = #{sessUserId}
WHERE BOARD.write_id = #{sessUserId}
)A
WHERE ROW_NUM > (#{pageNum} - 1) * #{pageResultCnt} AND ROW_NUM <= #{pageNum} * #{pageResultCnt} ORDER BY ROW_NUM
